# Load datasets
sym_des = pd.read_csv("datasets/symtoms_df.csv")
precautions = pd.read_csv("datasets/precautions_df.csv")
workout = pd.read_csv("datasets/workout_df.csv")
description = pd.read_csv("datasets/description.csv")
medications = pd.read_csv('datasets/medications.csv')
diets = pd.read_csv("datasets/diets.csv")

# Load model
svc = pickle.load(open('models/svc.pkl', 'rb'))

# Helper function to get additional information about the disease
def helper(dis):
    desc = description[description['Disease'] == dis]['Description'].values[0]
    
    # Handle missing or incorrectly named columns
    pre = precautions[precautions['Disease'] == dis][['Precaution_1', 'Precaution_2', 'Precaution_3', 'Precaution_4']].values[0]
    med = medications[medications['Disease'] == dis]['Medication'].values
    
    # Check for the existence of columns in diets DataFrame
    if 'Diet' in diets.columns:
        die = diets[diets['Disease'] == dis]['Diet'].values
    else:
        die = ["No diet information available"]
    
    if 'diets' in diets.columns:
        dit = diets[diets['Disease'] == dis]['diets'].values
    else:
        dit = ["No detailed diets available"]
    
    wrkout = workout[workout['disease'] == dis]['workout'].values
    return desc, pre, med, die, wrkout, dit

