# Prediction function to generate input vector and predict disease
def get_predicted_value(patient_symptoms):
    # Initialize an input vector with 132 zeros (matches model's expected input shape)
    input_vector = np.zeros(132)
    
    # Map provided symptoms to the corresponding indices in the input vector
    for item in patient_symptoms:
        if item in symptoms_dict:
            input_vector[symptoms_dict[item]] = 1  # Set feature to 1 if symptom is present
    
    # Predict disease and return result
    prediction_index = svc.predict([input_vector])[0]
    return diseases_list.get(prediction_index, "Unknown Disease")

# View to handle prediction request
def predict(request):
    if request.method == 'POST': 
        symptoms = request.POST.get('symptoms')
        if symptoms == "Symptoms":
            message = "Please either write symptoms or check for spelling errors"
            return render(request, 'index.html', {'message': message})
        else:
            # Split input symptoms and clean up whitespace
            user_symptoms = [s.strip() for s in symptoms.split(',')]
            
            # Predict disease based on user symptoms
            predicted_disease = get_predicted_value(user_symptoms)
            
            # Retrieve additional disease details using helper function
            dis_des, precautions, medications, rec_diet, workout, dit = helper(predicted_disease)
            
            # Render result with disease and related information
            return render(request, 'index.html', {
                'predicted_disease': predicted_disease,
                'dis_des': dis_des,
                'precautions': precautions,
                'medications': medications,
                'rec_diet': rec_diet,
                'workout': workout,
                'diets': dit,
            })
    return render(request, 'index.html')


def signup_view(request):
    if request.method == 'POST':
        username = request.POST['username']
        email = request.POST['email']
        password = request.POST['password']
        confirm_password = request.POST['confirm_password']
        
        if password == confirm_password:
            if User.objects.filter(username=username).exists():
                messages.error(request, "Username already exists")
            elif User.objects.filter(email=email).exists():
                messages.error(request, "Email already registered")
            else:
                user = User.objects.create_user(username=username, email=email, password=password)
                user.save()
                messages.success(request, "Account created successfully!")
                return redirect('login')
        else:
            messages.error(request, "Passwords do not match")
    
    return render(request, 'signup.html')

def login_view(request):
    if request.method == 'POST':
        username = request.POST['username']
        password = request.POST['password']
        
        user = authenticate(request, username=username, password=password)
        if user is not None:
            login(request, user)
            messages.success(request, "Logged in successfully")
            return redirect('index')  # Redirect to a home page or dashboard
        else:
            messages.error(request, "Invalid username or password")
    
    return render(request, 'login.html')

def logout_view(request):
    logout(request)
    messages.success(request, "Logged out successfully")
    return redirect('login')